import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { QuestionServiceService } from '../ServeforQuiz/question-service.service';
import { QuizServiceService } from '../ServeforQuiz/quiz-service.service';

@Component({
  selector: 'app-edit-question',
  templateUrl: './edit-question.component.html',
  styleUrls: ['./edit-question.component.css']
})
export class EditQuestionComponent implements OnInit {
  QuestionForm: any;
  QuizForm:any;
  RightAnswer: any[] = [];

  constructor(private fb: FormBuilder,
    private route_id: ActivatedRoute,
    private questionService: QuestionServiceService,
    private quizService:QuizServiceService) { }

  ngOnInit(): void {
    this.QuestionForm = this.fb.group({
      questionId: 0,
      questionName: ['', Validators.required],
      option1: ['', Validators.required],
      option2: ['', Validators.required],
      option3: ['', Validators.required],
      option4: ['', Validators.required],
      right_Answer: ['', Validators.required],
      mark: 0,
      quizId: 0
    });

    this.QuizForm = this.fb.group({

    });

    this.route_id.paramMap.subscribe({
      next: (re) => {
        const id = re.get('Id');
        if (id) {
          this.questionService.getQuestion(parseInt(id)).subscribe({
            next: (re) => {
              this.QuestionForm.patchValue(re);
              this.RightAnswer = [];
              if (this.QuestionForm.value.option1 !== '') {
                this.RightAnswer.push(this.QuestionForm.value.option1);
              }
              if (this.QuestionForm.value.option2 !== '') {
                this.RightAnswer.push(this.QuestionForm.value.option2);
              }
              if (this.QuestionForm.value.option3 !== '') {
                this.RightAnswer.push(this.QuestionForm.value.option3);
              }
              if (this.QuestionForm.value.option4 !== '') {
                this.RightAnswer.push(this.QuestionForm.value.option4);
              }
              console.log(this.QuestionForm.value);
            },
            error: (er) => {
              console.log(er);
            }
          });

          this.quizService.getQuiz(parseInt(id)).subscribe({
            next: (re) => {
              console.log(re);
            },
            error: (er) => {
              console.log(er);
            }
          });

          this.quizService.getAllQuiz().subscribe({
            next: (re) => {
              console.log(re);
            },
            error: (er) => {
              console.log(er);
            }
          });
        }
      },
      error: (er) => {
        console.log(er);
      }
    });
  }

  pushrightAnswer() {
    console.log('clicked');

    this.RightAnswer = [];
      this.RightAnswer.push(this.QuestionForm.value.option1);
    if (this.QuestionForm.value.Option2 !== ' ') {
      this.RightAnswer.push(this.QuestionForm.value.option2);
    }
    if (this.QuestionForm.value.Option3 !== ' ') {
      this.RightAnswer.push(this.QuestionForm.value.option3);
    }
    if (this.QuestionForm.value.Option4 !== ' ') {
      this.RightAnswer.push(this.QuestionForm.value.option4);
    }
  }

  updateQuestion(){
    console.log(this.QuestionForm.value);

  }

  deleteQuestion(){

  }
}
<div class="container">
    <form [formGroup]="QuestionForm">
        <div>
            <input formControlName="questionId" hidden>
        </div>
        <div class="col-md-12">
            <label class="form-label text-success">Question</label>
            <input type="text" class="form-control" formControlName="questionName">
        </div>
        <div class="col-md-12">
            <div class="row">
                <div class="col-md-6">
                    <label class="form-label">Option 1</label>
                    <input type="text" class="form-control" formControlName="option1">
                </div>
                <div class="col-md-6">
                    <label class="form-label">Option 2</label>
                    <input type="text" class="form-control" formControlName="option2">
                </div>
                <div class="col-md-6">
                    <label class="form-label">Option 3</label>
                    <input type="text" class="form-control" formControlName="option3">
                </div>
                <div class="col-md-6">
                    <label class="form-label">Option 4</label>
                    <input type="text" class="form-control" formControlName="option4">
                </div>
            </div>
        </div>
        <div class="col-md-12">
            <div class="row">
                <div class="col-md-8">
                    <label class="form-label">Right Answer</label>
                    <select class="form-select" aria-label="Default select example" formControlName="right_Answer" (click)="pushrightAnswer()">
                        <option *ngFor="let item of RightAnswer" value="{{item}}">{{item}}</option>
                    </select>
                </div>
                <div class="col-md-4">
                    <label class="form-label">Mark</label>
                    <input type="number" class="form-control" formControlName="mark">
                </div>
            </div>
        </div>
        <div class="mt-3">
            <a class="btn btn-success" (click)="updateQuestion()">Update</a>
            <a class="btn btn-danger ms-3" (click)="deleteQuestion()">Delete</a>
        </div>
    </form>
</div>
