import { Component, OnInit } from '@angular/core';
import { QuizQuestionServiceService } from '../ServeforQuiz/quiz-question-service.service';
import { QuestionServiceService } from '../ServeforQuiz/question-service.service';
import { ActivatedRoute, Route } from '@angular/router';

@Component({
  selector: 'app-question-view',
  templateUrl: './question-view.component.html',
  styleUrls: ['./question-view.component.css']
})
export class QuestionViewComponent implements OnInit {
  Question_Details:any[] = [];
  quizName:string = '';
  quizNumber:number = 0;
  quizQuestion: number = 0;
  id:any;
  hasdata:boolean = true;

  constructor(private Quiz_Question: QuizQuestionServiceService,
    private Question: QuestionServiceService, 
    private route_data: ActivatedRoute) { }

  ngOnInit(): void {

    this.route_data.paramMap.subscribe({
      next: (re)=>{
        const Id = re.get('Id');
        if(Id){
          this.Quiz_Question.GetQuizDetails(parseInt(Id)).subscribe({
            next: (re)=>{
              this.quizName=re[0].quizName
              this.quizNumber=re[0].number
              this.quizQuestion=re[0].total_Question
              // console.log(re)
            },
            error: (er)=>{
              console.log(er);
            }
          });

          this.Quiz_Question.GetQuestionDetails(parseInt(Id)).subscribe({
            next: (re)=>{
              if(re.length === 0){
                console.log('null');
                this.hasdata = false;              
              }else{
                this.Question_Details = re;
              }
              // console.log(re)
            },
            error: (er)=>{
              console.log(er);
            }
          });

        }
      }
    });
  }
}
<div class="container d-flex my-5 flex-wrap">
    <div class="w-75">
        <h1 class="text-center">{{quizName}} Quiz</h1>
    </div>    
    <div class="w-25">
        <h6 style="text-align: right;">Question : {{quizQuestion}}</h6>
        <h6 style="text-align: right;">Mark : {{quizNumber}}</h6>
    </div>

    <div class="card w-100 my-3 p-3" *ngIf="hasdata">
        <div *ngFor="let item of Question_Details">
            <div class="d-flex">
                <h6 class="w-75">{{item.questionName}}</h6>
                <h6 class="w-25" style="text-align: right;">{{item.mark}}</h6>
            </div>
            <div class="d-flex flex-wrap">
                <h6 class="w-50">A) {{item.option1}}</h6>
                <h6 class="w-50">B) {{item.option2}}</h6>
                <h6 class="w-50">C) {{item.option3}}</h6>
                <h6 class="w-50">D) {{item.option4}}</h6>
            </div>
            <div class="d-flex">
                <div class="w-75">Right Answer : <strong>{{item.right_Answer}}</strong></div>
                <div class="w-25" style="text-align: right;">
                    <a [routerLink]="['/editquestion',item.questionId,item.quizId]" class="btn btn-primary">Edit</a>
                </div>
            </div>
            <hr>
        </div>
    </div>
    <div class="card w-100 my-3 p-3 text-danger" *ngIf="!hasdata">
        **No Data To preview
    </div>
    <a href="/QuizView" class="btn btn-warning">Back</a> 
</div>
